$(function() {
	moveSearchOnMain();
	moveSearchOnSmall();
	miniNavbar();
	moveTOC();
	moveNsHeader();
	toggleSidebar();
	cleanFooterIcons();
	moveSidebarTogglers();
	backToTop();
});

const mq = window.matchMedia("(max-width: 1105px)");
const sb = window.matchMedia("(min-width: 1740px)");
const sel = $('.sidebar');

$(window).resize(function() {
	moveSearchOnSmall();
	miniNavbar();
	if (sb.matches) {
		$('.sidebar').removeClass('on-canvas');
	}
});

$( window ).scroll(function() {
    var fromTop = $(window).scrollTop();
    var offset = 240;

    if ($(this).scrollTop()) {
		$('#back-to-top').css('visibility', 'visible');
	} else {
		$('#back-to-top').css('visibility', 'hidden');
	}

	function setToWindowHeight(sel) {
		if(fromTop < 180 ){ 
			offset = 240 - fromTop;
		} else { 
			offset = 60; 
		}
		sel.animate({'min-height':  $(window).innerHeight(), 'bottom': 0, 'top': fromTop + 'px' }, 0, "easeOutExpo");
		$('#hf-nsheader-, #move-to-right-bar, #toc').animate({'top': offset + 'px'}, 600, "easeInOutQuad");
	}
	clearTimeout($.data(this, 'scrollTimer'));
    $.data(this, 'scrollTimer', setTimeout(function() {
		setToWindowHeight(sel);
	}, 150));

});


function moveSearchOnSmall() {
	var mp = mw.config.get( 'wgIsMainPage' );
	if (mq.matches) {
		$('#mainpage-search-wrapper').prepend($('#p-search'));
	} else {
		if (mp) { 
		} else {
			$('#non-mainpage-search-wrapper').prepend($('#p-search'));
		}
	}
	$('#p-search').css({'visibility':'visible'});
}
function miniNavbar() {
	if (mq.matches) {
		$('#mw-navigation').addClass('mini');
	} else {
		$('#mw-navigation').removeClass('mini');
	}
}
function moveSearchOnMain() {
        var mp = mw.config.get( 'wgIsMainPage' );
        if (mp) { 
             $('#mainpage-search-wrapper').detach().prependTo('#bodyContent').prepend($('#p-search'));
        }
        $('#p-search').css({'visibility':'visible'});
}
function moveTOC(){
	if($('#toc').length > 0){
		var toctitle = '<div class="nav-item"><div id="ttitle" class="nav-link px-3">Contents</div></div>';
		$('#toc').prepend(toctitle).detach().prependTo('#sidebar-right');
	}
	if($('#move-to-right-bar').length > 0){
		$('#move-to-right-bar').detach().appendTo('#sidebar-right');
	}
}
function miniDropdown(){
	if (mq.matches) {
		$('#mw-navigation.mini .dropdown-toggle').click( function(event) {
			event.preventDefault();
			event.stopPropagation();
			var activemenu = $(this).parent('div').find('.dropdown-menu');
			$(activemenu).toggle();
			$('#mw-navigation.mini .dropdown-menu').not(activemenu).hide();
		});
	} 
}
function toggleSidebar(){
	$('#sidebar-right-toggler').bind('click', function() {
		$('#sidebar-right-wrapper').toggleClass('on-canvas');
		$(this).find('i').toggleClass('fa-plus-square fa-minus-square');
	});
	$('#sidebar-left-toggler').bind('click', function() {
		$('#sidebar-left-wrapper').toggleClass('on-canvas');
		$(this).find('i').toggleClass('fa-plus-square fa-minus-square');
	});
}
function moveNsHeader(){
	$('#hf-nsheader-').detach().prependTo('#sidebar-left');
}
function cleanFooterIcons(){
	$('#footer-merlin .nav-link').text("");
	$('#footer-merlin').css('display','flex');
}
function moveSidebarTogglers(){
	var wrapper = $('#mw-navigation');
	$('#sidebar-left-toggler').detach().prependTo(wrapper);
	$('#sidebar-right-toggler').detach().appendTo(wrapper);
}
function backToTop() {
$('#back-to-top').click(function(e) {
        e.preventDefault();
        $('html, body').animate({
                scrollTop: "0"
        });
});
}

#back-to-top {
	background: var(--dark);
	border-radius: 50%;
	display: flex;
	justify-content: center;
	align-items: center;
	color: #fff;
	position: fixed;
	width: 64px;
	height: 64px;
	cursor: pointer;
	right: 20px;
	bottom: 160px;
	z-index: 2000;
        opacity: .7;
}
#back-to-top:hover {
	box-shadow: 0 8px 24px rgba(0, 0, 0, .16) !important;
        opacity: 1;
}

