 /**
  * Text processing
  */
function consoleToHTML(consoleText) {
	var html = "";

	if (consoleText) {
		html = consoleText;
		html = html.replace(/(?:\r\n|\r|\n)/g, '<br/>');
		html = html.replace(/\s/g, '&nbsp;');
	}

	return html;
}

 /**
  * Subdomain detection
  */
function subdomain() {
    var host = window.location.hostname;
    var first = host.split('.')[0].toLowerCase();
    return first;
}

