$(function() {
	backToTop();
});

function backToTop() {
$('#back-to-top').click(function(e) {
        e.preventDefault();
        $('html, body').animate({
                scrollTop: "0"
        });
});
$(window).scroll(function() {
        if ($(this).scrollTop()) {
                $('#back-to-top').css('visibility', 'visible');
        } else {
                $('#back-to-top').css('visibility', 'hidden');
        }
});
}

